import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';

import Auction from "./auction";

const useStyles = makeStyles(theme => ({
	root: {
		flexGrow: 1,
	},
}));

export default function AuctionManager() {
    const classes = useStyles();
    const [state, setState] = React.useState({
        auctions: [],
    });

    //deploy the contract
	function handleDeploy(event) {
        setState(oldState => ({
            ...oldState,
            auctions: state.auctions.concat(Math.random()),
        }));
		console.log(state);
    }

    return(
        <React.Fragment>
            <Grid container className={classes.root} spacing={2} direction="row" justify="flex-start" alignItems="center">
                {state.auctions.map(addr => (
                    <Grid key={addr} item>
                       <Auction creation_block={11} />
                    </Grid>
                ))}
            </Grid>

            <Button onClick={handleDeploy} variant="contained" className={classes.button}>Add!</Button>
        </React.Fragment>
    );
}
