import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles(theme => ({
	root: {
		display: 'flex',
		flexWrap: 'wrap',
	},

	formControl: {
		margin: theme.spacing(1),
		minWidth: 120,
	},

	selectEmpty: {
		marginTop: theme.spacing(2),
	},
}));

export default function ContractCreator() {
	const classes = useStyles();
	const [values, setValues] = React.useState({
		auctionType: '',
		reservePrice: '',
		name:''
	});

	function handleChange(event) {
		setValues(oldValues => ({
		...oldValues,
		[event.target.name]: event.target.value,
		}));
	}

	return (
		<form className={classes.root} autoComplete="off">
			<FormControl className={classes.formControl}>
				<InputLabel htmlFor="auction-type">Auction Type</InputLabel>
				<Select
					value={values.auctionType}
					onChange={handleChange}
					inputProps={{ name: 'auctionType', id: 'auction-type',}}
				>
					<MenuItem value={10}>Vickery</MenuItem>
					<MenuItem value={20}>English</MenuItem>
				</Select>
			</FormControl>

			<FormControl className={classes.formControl}>
				<TextField 
					id="reserve-price" 
					label="Reserve Price (wei)"
					className={classes.textField}
        			margin="none"
      			/>
			</FormControl>
		</form>
	);
}
