import React, { Component } from 'react';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core'

import NavBar from "./components/navbar";
import ContractCreator from "./components/auction_creator";
import AuctionManager from "./components/auction_manager";

const styles = theme => ({
	root: {
		flexGrow: 1,
	},

	control: {
		padding: theme.spacing(2),
	},

	paper: {
		padding: theme.spacing(3),
    	textAlign: 'center',
    	color: theme.palette.text.secondary,
	},
});

class App extends Component {
	state = {
		auctions: [],
	}

	//add to the auctions array a new auction
    handleDeploy = (event) => {
        this.setState(oldState => ({
            ...oldState,
            auctions: this.state.auctions.concat(Math.random()),
        }));
	}

	render(){
		const { classes } = this.props
		return (
			<React.Fragment>
				<NavBar/>
				<main>
					<Container maxWidth="lg">
						<Grid container className={classes.root} spacing={2} justify = "center">
							<Grid item xs={10} sm={10}>
								<Paper className={classes.paper}>
									bidder
									<AuctionManager auctions={this.state.auctions} onDeploy={this.handleDeploy}/>
								</Paper>
							</Grid>
							<Grid item xs={10} sm={10}>
								<Paper className={classes.paper}>
									seller
									<ContractCreator onDeploy={this.handleDeploy}/>
								</Paper>
							</Grid>
							<Grid item xs={10} sm={10}>
								<Paper className={classes.paper}>auctioneer</Paper>
							</Grid>
						</Grid>
					</Container>

					<Container maxWidth="sm">
						<Box my={4}>
							<Typography variant="body2" color="textSecondary" align="center">
								P2P 2018/2019 Final Project made by Remo Andreoli
							</Typography>
						</Box>
					</Container>
				</main>
			</React.Fragment>
		);
	}
}

export default withStyles(styles)(App);


