import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import FormHelperText from '@material-ui/core/FormHelperText';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles(theme => ({
	root: {
		display: 'flex',
		flexWrap: 'wrap',
	},

	formControl: {
		margin: theme.spacing(1),
		minWidth: 120,
	},

	selectEmpty: {
		marginTop: theme.spacing(2),
	},
}));

export default function ContractCreator() {
	const classes = useStyles();
	const [values, setValues] = React.useState({
		auctionType: '',
		reservePrice: '',
		name:''
	});

	function handleChange(event) {
		setValues(oldValues => ({
		...oldValues,
		[event.target.name]: event.target.value,
		}));
	}

	return (
		<React.Fragment>
			<h1>Vickery</h1>
			<form className={classes.root} autoComplete="off">
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk-reserve-price" 
						label="Reserve Price"
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(wei)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk-buyout-price" 
						label="Buy Out"
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(wei)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk-unchallenged-length" 
						label="Unchallenged Length"
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(blocks)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk-increment" 
						label="Min. Increment (wei)"
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(wei)</FormHelperText>
				</FormControl>
				<Button className={classes.button}>Deploy!</Button>
			</form>

			<h1>English</h1>
			<form className={classes.root} autoComplete="off">
			<FormControl className={classes.formControl}>
				<TextField 
					id="en-reserve-price" 
					label="Reserve Price (wei)"
					className={classes.textField}
					margin="none"
				/>
			</FormControl>
			</form>
		</React.Fragment>
	);
}

