import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import { getThemeProps } from '@material-ui/styles';

const useStyles = makeStyles(theme => ({
	root: {
		display: 'flex',
		flexWrap: 'wrap',
	},

	formControl: {
		margin: theme.spacing(1),
		minWidth: 120,
	},

	header:{
		margin: 0,
	},

	button:{
		margin: theme.spacing(1),
	},
}));

export default function ContractCreator(props) {
	const classes = useStyles();

	//state of the text field used to configure the contract
	const [state, setState] = React.useState({
		en_reserve_price: '',
		en_buyout_price: '',
		en_increment: '',
		en_unchallenged_length: '',

		vk_reserve_price: '',
		vk_min_deposit: '',
		vk_commitment_length: '',
		vk_withdrawal_length: '',
		vk_opening_length: '',
	});

	//handle the state in change due to user input
	function handleChange(event) {
		event.persist();
		setState(oldState => ({
		  ...oldState,
		  [event.target.id]: event.target.value,
		}));

		if (event.target.value.match(/[a-z]/i)) {
			console.log("wrong!");
		}
	}

	//deploy the contract
	function handleDeploy(event) {
		console.log(state);

		props.onDeploy();
	}

	return (
		<React.Fragment>
			<h2 className={classes.header}>English</h2>
			<form className={classes.root} autoComplete="off">
				<FormControl className={classes.formControl}>
					<TextField 
						id="en_reserve_price" 
						label="Reserve Price"
          				onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(wei)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="en_buyout_price" 
						label="Buy Out"
						onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(wei)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="en_increment" 
						label="Min. Increment"
						onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(wei)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="en_unchallenged_length" 
						label="Unchallenged Length"
						onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(blocks)</FormHelperText>
				</FormControl>
			</form>
			<Button onClick={handleDeploy} variant="contained" className={classes.button}>Deploy!</Button>

			<Divider variant="middle" />

			<h2 className={classes.header}>Vickery</h2>
			<form className={classes.root} autoComplete="off">
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk_reserve_price" 
						label="Reserve Price"
						onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(wei)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk_min_deposit" 
						label="Min. Deposit"
						onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(wei)</FormHelperText>
				</FormControl>
			</form>
			<form className={classes.root} autoComplete="off">
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk_commitment_length" 
						label="Commitment Phase Length"
						onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(blocks)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk_withdrawal_length" 
						label="Withdrawal Phase Length"
						onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(blocks)</FormHelperText>
				</FormControl>
				<FormControl className={classes.formControl}>
					<TextField 
						id="vk_opening_length" 
						label="Opening Phase Length"
						onChange={handleChange}
						className={classes.textField}
						margin="none"
					/>
					<FormHelperText>(blocks)</FormHelperText>
				</FormControl>
			</form>
			<Button onClick={handleDeploy} variant="contained" className={classes.button}>Deploy!</Button>
		</React.Fragment>
	);
}
