import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import FilledInput from '@material-ui/core/FilledInput';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles(theme => ({
	root: {
		display: 'flex',
		flexWrap: 'wrap',
	},
	formControl: {
		margin: theme.spacing(1),
		minWidth: 120,
	},
	selectEmpty: {
		marginTop: theme.spacing(2),
	},
}));

export default function ContractCreator() {
	const classes = useStyles();
	const [values, setValues] = React.useState({
		auctionType: '',
    	name: 'hai',
	});

	function handleChange(event) {
		setValues(oldValues => ({
		...oldValues,
		[event.target.name]: event.target.value,
		}));
	}

	return (
		<form className={classes.root} autoComplete="off">
		<FormControl className={classes.formControl}>
			<InputLabel htmlFor="auction-type">Auction Type</InputLabel>
			<Select value={values.auctionType} onChange={handleChange} input={<Input name="auction-type-input" id="auction-type-input" />}>
				<MenuItem value={10}>Vickery</MenuItem>
				<MenuItem value={20}>English</MenuItem>
			</Select>
			<FormHelperText>Type of auction</FormHelperText>
		</FormControl>

		<FormControl className={classes.formControl}>
			<InputLabel htmlFor="reserve-price">Auction Type</InputLabel>
			<Select value={values.auctionType} onChange={handleChange} input={<Input name="auction-type-input" id="auction-type-input" />}>
				<MenuItem value={10}>Vickery</MenuItem>
				<MenuItem value={20}>English</MenuItem>
			</Select>
			<FormHelperText>Some important helper text</FormHelperText>
		</FormControl>
		</form>
	);
}
